public class ausgabenAddition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("" + 5 + 6); // Ausgabe = 56
		System.out.println(5 + 6); // Ausgabe = 11
		System.out.println(5 + "" + 6); // Ausgabe 56
		System.out.println(5 + 6 + ""); // Ausgabe 11
	}

}
